<?session_start();if(isset($_SESSION['euser']) && $_SESSION['euser'] == true){require "categories.php";require "subcategories.php";//$optionValue = 'art';if(isset($_POST['optionValue'])){  $ov = substr((string)$_POST['optionValue'],0,17);  if(is_string($ov)){	$ov = strtolower(preg_replace('/[^a-z]/i','',$ov));	if($ov != ''){ $optionValue = $ov; }  }}if(isset($optionValue)){if(isset($categories[$optionValue])){  $_SESSION['cat'] = $optionValue;  $subcategorySelect = '<select id="selectSubcategory" name="subcategorylink" onchange="enableaskbt(this);"><option value="choose" selected="selected">Select subcategory</option>';  foreach($subcategories[$optionValue] as $key => $value){    $subcategorySelect = $subcategorySelect.'<option value="'.$key.'">'.$value.'</option>';    //print('$key: '.$key.' | $value: '.$value.'<br />');  }  $subcategorySelect = $subcategorySelect.'</select>';  print($subcategorySelect);}else{  unset($_SESSION['cat']);  unset($_SESSION['subcat']);  $subcategorySelect = '<select disabled="disabled"><option value="choose" selected="selected">Select subcategory</option></select>';  print($subcategorySelect);}}}?>